mapper.select:
select * 
from (
		select post_id, series_id, user_email, img_id, post_title,
			   post_content, post_regdate, post_viewcount, post_summary, post_img_list, rownum rnum
		from aka_post
		order by post_id desc
	 )

	where #{start} <= rnum and rnum <= #{end}

SELECT	*
FROM	AKA_POST
WHERE	POST_ID = #{ post_id }

SELECT  USER_NICKNAME
FROM	AKA_USER
WHERE	USER_EMAIL	=	#{ post_email }

SELECT	SERIES_TITLE
FROM	AKA_SERIES
WHERE	SERIES_ID	=	#{ post_seriesId }

mapper.update:
UPDATE	AKA_POST
SET		POST_VIEWCOUNT	=	POST_VIEWCOUNT + 1
WHERE 	POST_ID	=	#{post_id}
